mapper.select:
SELECT `index` AS `index`, `movie_index` AS `movieIndex`, `image_name` AS `imageName`, `image_size` AS `imageSize`, `image_type` AS `imageType`, `image_data` AS `imageData`, `category` AS `category`, `created_at` AS `createdAt`, `deleted_flag` AS `isDeleted` FROM `movie_sq`.`movie_images` WHERE BINARY `movie_index` = #{index} AND `deleted_flag` != true
AND `category` = 'PICTURE'
LIMIT 1;
